import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Card, CardContent, CardActionArea, CardMedia, Typography, Grid, Container, Item } from '@material-ui/core/';

var projectCountz = 0;

var array= [];     

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        backgroundColor: theme.palette.primary.light,
    },
    card: {
        maxWidth: 250,
        margin: 0,
    },
    media: {
        height: 160,
    },
}));


export default function Portfolio(props) {
    const classes = useStyles();

    window.onresize = function(event) {
        var width = document.getElementById.clientWidth;;
        var rowFit = Math.floor(width/300);
        var newMarg = (width-(rowFit*300))/(rowFit*2)
        for (let i of array){
            var element = document.getElementById(i);
            element.style.width = String(newMarg) + 'px';
            element.style.float = "left"
            console.log(rowFit);        
        }
    };

    if (props.data) {
        var projects = props.data.projects.map(
        function (projects) {
            array.push(projects.title+"@projectID");
            var projectImage = 'images/portfolio/' + projects.image;
            return (
                <div key={projects.title} style={{le:'300px', float:"center"}} id={projects.title+"@projectID"} className="columns portfolio-item">
                    <div class="card">
                        <div class="face face1">
                            <div class="content">
                                <span class="stars"></span>
                                <h2 class="javascript">Github Projects</h2>
                                <p class="javascript"> Please click on this site to see more of my projects, finished and unfinished :) </p>
                            </div>
                        </div>
                        <div class="face face2">
                            <h2>)))))1</h2>
                        </div>
                    </div>
                </div>
            )
        })
    }

    const myelement = (
        <div class="card">
            <div class="face face1">
                <div class="content">
                    <span class="stars"></span>
                    <h2 class="javascript">Github Projects</h2>
                    <p class="javascript"> Please click on this site to see more of my projects, finished and unfinished :) </p>
                    </div>
                </div>
            <div class="face face2">
                <h2>01</h2>
            </div>
        </div>
    );

    return (
        <section className={classes.root} id="portfolio">
            <Container alignItems="center" className="row">
                <div id="portfolio-wrapper" >
                        {projects}
                </div>
            </Container>
        </section>
    );
}

